                </select>
                <button onClick={clickAddSL} className="btn AddPlan">追 加</button>
              </div>
              <div className="SelectBox">
                <select onChange={(e: React.ChangeEvent<HTMLSelectElement>) =>{ setSGName(e.target.value)}} defaultValue={SG[0].name}>
